import React, { Component } from 'react';

class EditUser extends Component {

    constructor(props) {
        super(props);
        this.state = {
            id : '',
            fullName : this.props.userEditObject2,
            tel : this.props.userEditObject2.tel,
            permission : this.props.userEditObject2.permission
        }
    }


    isChange = (event) => {
        console.log(this.props.userEditObject2);
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]:value
        });
    }
    //this.props.changeEditUserStatus
    editClick = () => {

        //this.props.changeEditUserStatus();
        this.setState({
            id : this.props.userEditObject2.id
        });
        console.log("props la : " + this.state.id);

        var info = {}
        // info.id = this.state.id;
        // info.fullName = this.state.fullName;
        // info.tel = this.state.tel;
        // info.permission = this.state.permission;
        // console.log(info.tel);



        this.props.getUserEditInfo(info);

    }


    //this.props.editUserStatus
    //this.props.userEditObject2
    //this.props.getUserEditInfo(info)
    showEditUserStatus = () => {
        if (this.props.editUserStatus === true) {            
            return (
                <div className="col">
                    <div className="card border-warning mb-3">
                        <div className="card-header bg-warning text-center">Sửa thông tin User</div>
                        <div className="card-body text-warning">
                            <form>   
                                <div className="form-group">
                                    <input onChange={(event) => this.isChange(event)} defaultValue={this.props.userEditObject2.fullName} name="fullName" type="text"  className="form-control"/>
                                </div> 
                                <div className="form-group">
                                    <input onChange={(event) => this.isChange(event)} defaultValue={this.props.userEditObject2.tel} name="tel" type="text"  className="form-control" />
                                </div>
                                <div className="form-group">
                                    <div className="form-group">
                                    <select onChange={(event) => this.isChange(event)} name="permission" className="custom-select" required defaultValue={this.props.userEditObject2.permission}>
                                        <option value>Chọn quyền mặc định</option>
                                        <option value= {1}>Admin</option>
                                        <option value= {2}>Coordinator</option>
                                        <option value= {3}>Employee</option>
                                        <option value= {4}>Normal</option>
                                    </select>
                                    </div>
                                </div>
                                <div className="form-group">
                                    <button type="button"  className="btn btn-warning btn-block" onClick={() => this.editClick()}> <i className="fa fa-edit  pr-1" /> Sửa</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            );
        }
    }

    render() {
            console.log("props: " + this.props.userEditObject2.fullName);  
        return (
            <div>
                {this.showEditUserStatus()}
            </div>
        );
    }
}

export default EditUser;
