import React, { Component } from 'react';


class SearchForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tempValue: ''
        }
    }

    showBtn = () => {
        if(this.props.showBtn === true){
            return (
                <button type="button" className="btn btn-outline-secondary mb-2" onClick={() => this.props.changeState()} > <i className="fa fa-remove  pr-1" /> Đóng lại</button>
            );
        }
        else {
            return (
                <button type="button" className="btn btn-outline-info mb-2" onClick={() => this.props.changeState()} > <i className="fa fa-plus  pr-1" /> Thêm User</button>
                );
        }
    }
    isChange = (event) => {
        console.log(event.target.value);
    }
    render() {
        return (
            <div className="col-12 col-lg-6 formSearch">
                <div className="form-group">
                    <div className="btn-group btn-group-search">
                        <input type="text" className="form-control" placeholder="Nhập từ khoá.." onChange={(event)=>this.isChange(event)} />
                        <button className="btn btn-info" onClick={this.props.checkConnectProps}><i className="fa fa-search " /> Tìm</button>
                    </div>
                </div>
                <div className="form-group">
                    <div className="btn-group btn-group-addclose">
                        {this.showBtn()}
                    </div>
                </div> 
            </div>
        );
    }
}

export default SearchForm;
