import React, { Component } from 'react';

showBtn = () => {
    if(this.props.showBtn === true){
        return (
            <button type="button" className="btn btn-outline-secondary mb-2" onClick={() => this.props.changeState()} >Đóng lại</button>
        );
    }
    else {
        return (
            <button type="button" className="btn btn-outline-info mb-2" onClick={() => this.props.changeState()} >Thêm mới</button>
            );
    }
}
class SearchForm extends Component {
    render() {
        return (
            <div className="col-12 col-lg-6 formSearch">
                <div className="form-group">
                    <div className="btn-group">
                        <input type="text" className="form-control" placeholder="Nhập từ khoá.." />
                        <button className="btn btn-info"><i className="fa fa-search" /> Tìm</button>
                    </div>
                </div>
                <div className="form-group">
                    <div className="btn-group">
                    </div>
                </div> 
            </div>

        );
    }
}

export default SearchForm;
