import React, { Component } from 'react';

class AddUser extends Component {

    checkStateForm = () => {
        if(this.props.showAddForm === true){
            return (
                <div className="col-12 col-lg-3">
                    <div className="card border-info mb-3">
                        <div className="card-header bg-info text-white">Thêm mới User vào hệ thống</div>
                        <div className="card-body text-info">
                            <div className="form-group">
                                <input type="text"  className="form-control" placeholder="Tên user" />
                            </div> 
                            <div className="form-group">
                                <input type="text"  className="form-control" placeholder="Số điện thoại" />
                            </div>
                            <div className="form-group">
                                <div className="form-group">
                                <select className="custom-select" >
                                    <option defaultValue>Chọn quyền mặc định</option>
                                    <option value>Admin</option>
                                    <option value>Coordinator</option>
                                    <option value>Employee</option>
                                    <option value>Normal</option>
                                </select>
                                </div>
                            </div>
                            <div className="form-group">
                                <button type="button"  className="btn btn-info btn-block"> <i className="fa fa-plus  pr-1" /> Thêm mới</button>
                            </div>
                        </div>
                    </div>
                    </div>
            );
        }
    }

    render() {
        // console.log(this.props.showAddForm);

        return (      
                this.checkStateForm()
        );
    }
}

export default AddUser;
