import React, { Component } from 'react';

class EditUser extends Component {

    constructor(props) {
        super(props);
        this.state = {
            id: this.props.userEditObject.id ,
            fullName: this.props.userEditObject.fullName,
            tel: this.props.userEditObject.tel,
            permission: this.props.userEditObject.permission
        }
    }

    isChange = (event) => {
        console.log(this.props.userEditObject);
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]:value
        });
    }
    //this.props.changeEditUserStatus
    editClick = () => {
        this.props.changeEditUserStatus();

        var info = {}
        info.id = this.state.id;
        info.fullName = this.state.fullName;
        info.tel = this.state.tel;
        info.permission = this.state.permission;

        this.props.getUserEditInfo(info);
    }


    //this.props.editUserStatus
    //this.props.userEditObject
    //this.props.getUserEditInfo(info)
    showEditUserStatus = () => {
        if (this.props.editUserStatus === true) {            
            return (
                <div className="col">
                    <div className="card border-warning mb-3">
                        <div className="card-header bg-warning text-center">Sửa thông tin User</div>
                        <div className="card-body text-warning">
                            <form>   
                                <div className="form-group">
                                    <input onChange={(event) => this.isChange(event)} defaultValue={this.props.userEditObject.fullName} name="fullName" type="text"  className="form-control" placeholder="Tên user"/>
                                </div> 
                                <div className="form-group">
                                    <input onChange={(event) => this.isChange(event)} defaultValue={this.props.userEditObject.tel} name="tel" type="text"  className="form-control" placeholder="Số điện thoại"/>
                                </div>
                                <div className="form-group">
                                    <div className="form-group">
                                    <select onChange={(event) => this.isChange(event)} name="permission" className="custom-select" required defaultValue={this.props.userEditObject.permission}>
                                        <option value>Chọn quyền mặc định</option>
                                        <option value= {1}>Admin</option>
                                        <option value= {2}>Coordinator</option>
                                        <option value= {3}>Employee</option>
                                        <option value= {4}>Normal</option>
                                    </select>
                                    </div>
                                </div>
                                <div className="form-group">
                                    <button type="button"  className="btn btn-warning btn-block" onClick={() => this.editClick()}> <i className="fa fa-edit  pr-1" /> Sửa</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            );
        }
    }
    render() {
        return (
            <div>
                {this.showEditUserStatus()}
            </div>
        );
    }
}

export default EditUser;
