import React, { Component } from 'react';

class EditUser extends Component {

    //this.props.changeEditUserStatus

    //this.props.editUserStatus
    showEditUserStatus = () => {
        if (this.props.editUserStatus === true) {
            return (
                <div className="col">
                    <div className="card border-warning mb-3">
                        <div className="card-header bg-warning text-center">Sửa thông tin User</div>
                        <div className="card-body text-warning">
                            <form>
                                <div className="form-group">
                                    <input name="fullName" type="text"  className="form-control" placeholder="Tên user"/>
                                </div> 
                                <div className="form-group">
                                    <input name="tel" type="text"  className="form-control" placeholder="Số điện thoại"/>
                                </div>
                                <div className="form-group">
                                    <div className="form-group">
                                    <select name="permission" className="custom-select" required>
                                        <option value>Chọn quyền mặc định</option>
                                        <option value= {1}>Admin</option>
                                        <option value= {2}>Coordinator</option>
                                        <option value= {3}>Employee</option>
                                        <option value= {4}>Normal</option>
                                    </select>
                                    </div>
                                </div>
                                <div className="form-group">
                                    <button type="reset"  className="btn btn-warning btn-block" onClick={() => this.props.changeEditUserStatus()}> <i className="fa fa-edit  pr-1" /> Sửa</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            );
        }
    }
    render() {
        return (
            <div>
                {this.showEditUserStatus()}
            </div>
        );
    }
}

export default EditUser;
