import React, { Component } from 'react';

class AddUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            stateEdit : false
        }
    }
    showBtn = () => {
        if(this.state.stateEdit === true){
            return (
                <button type="button" className="btn btn-outline-secondary btn-block mb-2" onClick={() => this.changeStateBtn()} >Đóng lại</button>
            );
        }
        else {
            return (
                <button type="button" className="btn btn-outline-info btn-block mb-2" onClick={() => this.changeStateBtn()} >Thêm mới</button>
            );
        }
    }
    changeStateBtn = () => {
        this.setState ({
            stateEdit : !this.state.stateEdit
        })
    }
    showForm = () => {
        if(this.state.stateEdit === true){
            return (
                    <div className="card border-info mb-3">
                        <div className="card-header bg-info text-white">Thêm mới User vào hệ thống</div>
                        <div className="card-body text-info">
                            <div className="form-group">
                                <input type="text"  className="form-control" placeholder="Tên user" />
                            </div> 
                            <div className="form-group">
                                <input type="text"  className="form-control" placeholder="Số điện thoại" />
                            </div>
                            <div className="form-group">
                                <div className="form-group">
                                <select className="custom-select" >
                                    <option defaultValue>Chọn quyền mặc định</option>
                                    <option value>Admin</option>
                                    <option value>Coordinator</option>
                                    <option value>Employee</option>
                                    <option value>Normal</option>
                                </select>
                                </div>
                            </div>
                            <div className="form-group">
                                <button type="button"  className="btn btn-info btn-block">Thêm mới</button>
                            </div>
                        </div>
                    </div>
                );
        }
    }   
    render() {
        return (
            <div className="col-12 col-lg-3">
                {this.showBtn()}
                {this.showForm()}
            </div>

        );
    }
}

export default AddUser;
